DROP PROCEDURE `sp_delete_million_rows_with_pause`; CREATE DEFINER=`sqlusername`@`%` PROCEDURE `sp_delete_million_rows_with_pause`() NOT DETERMINISTIC CONTAINS SQL SQL SECURITY DEFINER BEGIN declare v_max int unsigned default 100; declare v_counter int unsigned default 0; while v_counter < v_max do DELETE FROM `your_table` where id = 12345 ORDER BY id LIMIT 10000; SET v_counter=v_counter+1; DO SLEEP(5); end while; END
